public static <ENTITY extends _Mapped<?>> ENTITY newPersistedInstance(
        final EntityManager entityManager, final Class<ENTITY> entityClass,
        final Supplier<? extends ENTITY> entitySupplier)
        throws Exception {
    final ENTITY entityInstance = entitySupplier.get();
    if (entityInstance == null) {
        return null;
    }
    final Metamodel metamodel = entityManager.getMetamodel();
    final EntityType<ENTITY> entityType = metamodel.entity(entityClass);
    for (final SingularAttribute<? super ENTITY, ?> attribute : entityType.getSingularAttributes()) {
        if (!attribute.isAssociation()) {
            continue;
        }
        if (attribute.isOptional() && current().nextBoolean()) {
            // optional = true 라면 (랜덤하게) 건너뛴다.
            continue;
        }
        final Class<?> javaType = attribute.getJavaType();
        final Type<?> type = attribute.getType();
        log.debug("javaType: {}", type);
        log.debug("type: {}", type);
        Object attributeValue = null;
        if (javaType != entityClass) {
            attributeValue = newPersistedInstance(entityManager, javaType.asSubclass(_Mapped.class));
            if (attributeValue == null) {
                log.warn("failed to get a persisted instance of {}", javaType);
                return null;
            }
        }
        final String attributeName = attribute.getName();
        final PropertyDescriptor propertyDescriptor = BeanUtils.getPropertyDescriptor(entityClass, attributeName);
        if (propertyDescriptor == null) {
            log.warn("no property descriptor for {} on {}", attributeName, entityInstance);
            continue;
        }
        final Method writeMethod = propertyDescriptor.getWriteMethod();
        if (writeMethod == null) {
            log.warn("no write method from {}", propertyDescriptor);
            continue;
        }
        if (!writeMethod.isAccessible()) {
            writeMethod.setAccessible(true);
        }
        writeMethod.invoke(entityInstance, attributeValue);
    }
    entityManager.persist(entityInstance);
    return entityInstance;
}
